def recover_chat_senders(input_chats):
    """
    Given input_chats = [users, messages] where users is a list of usernames
    and messages is a list of strings (either "sender: text" or "?: text"),
    recover senders for all messages or return ["Impossible"] if not possible.
    """
    users, messages = input_chats
    n = len(messages)
    U = set(users)

    # Parse known senders and texts; build neighbor set
    senders = []
    texts = []
    neighbors = [set() for _ in range(n)]
    for i, m in enumerate(messages):
        idx = m.find(':')
        s = m[:idx]
        t = m[idx+1:]
        texts.append(t)
        senders.append(s if s != '?' else None)
        # build neighbor relation later

    # Build neighbor indices (adjacency list for the message path)
    for i in range(n):
        if i-1 >= 0:
            neighbors[i].add(i-1)
        if i+1 < n:
            neighbors[i].add(i+1)

    # For each message i, maintain possible_senders[i]: set of usernames
    possible = [None] * n
    for i in range(n):
        if senders[i] is not None:
            # known sender
            if senders[i] not in U:
                return ["Impossible"]
            possible[i] = {senders[i]}
        else:
            # unknown
            # those not mentioned in text
            mentioned = set(u for u in U if u in texts[i])
            possible[i] = U - mentioned
            if not possible[i]:
                return ["Impossible"]

    # Constraint propagation (AC-3 like)
    # We keep edges between every pair of neighboring messages
    from collections import deque
    edge_queue = deque()
    for i in range(n):
        for j in neighbors[i]:
            edge_queue.append((i, j))

    def prune(i, j):
        """
        Remove from possible[i] any x that does not have
        any y in possible[j] with y != x.
        Return True if possible[i] was changed.
        """
        changed = False
        to_remove = set()
        pj = possible[j]
        # Precompute for efficiency: if pj has only one value
        if len(pj) == 1:
            uniq = next(iter(pj))
            for x in possible[i]:
                if x == uniq:
                    to_remove.add(x)
        else:
            # check for each x in possible[i]
            for x in possible[i]:
                if len(pj) == 1 and x in pj:
                    to_remove.add(x)
                elif len(pj) > 1 and x not in pj:
                    # y in pj\{x} exists
                    continue
                else:
                    # x in pj and nothing else => remove
                    # or theoretically stray case
                    to_remove.add(x)
        if not to_remove:
            return False
        possible[i] -= to_remove
        return True

    # Implement AC-3
    while edge_queue:
        i, j = edge_queue.popleft()
        if prune(i, j):
            # Domain at i reduced
            if not possible[i]:
                return ["Impossible"]
            for k in neighbors[i]:
                if k != j:
                    edge_queue.append((k, i))

    # After propagation, assign senders by greedy consistent choice
    assignment = [None] * n
    # pick for message 0
    assignment[0] = next(iter(possible[0]))
    # pick for each next message
    for i in range(1, n):
        # pick any in possible[i] different from previous
        found = False
        for cand in possible[i]:
            if cand != assignment[i-1]:
                assignment[i] = cand
                found = True
                break
        if not found:
            # No valid assignment step (shouldn't happen if AC-3 succeeded)
            return ["Impossible"]

    # Output recovered messages
    return [
        f"{assignment[i]}:{texts[i]}"
        for i in range(n)
    ]
