def min_hits_to_break_bricks(W1, W2, W3, S):
    INF = 10**9
    # dp[u][r]: minimum hits to break up mask u, with reverse parity r (0=normal, 1=reversed)
    dp = [ [INF]*2 for _ in range(8) ]
    dp[0][0] = 0
    dp[0][1] = 0
    # widths and their prefix sums
    W = [W1, W2, W3]
    P = [0]*(4)
    for i in range(3):
        P[i+1] = P[i] + W[i]
    total_W = P[3]
    all_bricks = 0b111
    for u in range(0, 8):
        # Only process reachable states
        for r in (0, 1):
            if dp[u][r] == INF:
                continue
            # Compute stack in current orientation
            rem = []
            for i in range(3):
                if not (u & (1<<i)):
                    rem.append(i)
            if r == 1:
                rem.reverse()
            K = len(rem)
            # Attempt hits removing k bricks from top
            # For k = 0..K:
            #   k=K => all removed, sum = total_W - sum_current_u
            #   k<K => sum = sum of first k bricks in rem
            sum_u = sum(W[i] for i in range(3) if (u & (1<<i)))
            # Precompute prefix sums in order
            pf = [0]*(K+1)
            for j in range(K):
                pf[j+1] = pf[j] + W[ rem[j] ]
            for k in range(0, K+1):
                if k < K:
                    s = pf[k]
                else:
                    s = total_W - sum_u
                if s > S:
                    continue
                if k < K:
                    # Remove first k bricks
                    new_u = u | sum((1<<rem[j]) for j in range(k))
                else:
                    new_u = all_bricks
                if dp[new_u][r] > dp[u][r] + 1:
                    dp[new_u][r] = dp[u][r] + 1
            # Reversal option: flip orientation (no cost)
            if dp[u][1 - r] > dp[u][r]:
                dp[u][1 - r] = dp[u][r]
    return dp[all_bricks][0]
