def print_nums(*args):
    # If no arguments, return '0'
    if not args:
        return "0"

    # Convert integers to strings
    strs = list(map(str, args))
    # Compute width based on the smallest of max length and second max length (or max if only one item)
    lengths = sorted((len(s) for s in strs), reverse=True)
    max_len = lengths[0]
    if len(lengths) > 1:
        second_max_len = lengths[1]
        width = min(max_len, second_max_len)
    else:
        # Only one element: its length
        width = max_len

    # Special case: if width == 1 and at least one number has width == 1, print all but the 1-width item as-is, that one padded to width=3
    if width == 1 and any(len(s) == 1 for s in strs):
        lines = []
        for s in strs:
            if len(s) == 1:
                lines.append(s.zfill(3))
            else:
                lines.append(s)
        return "\n".join(lines)

    # Otherwise pad all numbers (even those of max_len) to width, except 1-digit ones which get width=3
    lines = []
    for s in strs:
        if len(s) == 1:
            lines.append(s.zfill(3))
        else:
            lines.append(s.zfill(width))
