def case_id(c_str):
    # Helper to check for capitalized words (second part of kebab)
    def is_capitalized_word(word):
        return word and word[0].isupper() and word[1:].islower()

    # Snake: contains '_' and only lowercase letters
    if '_' in c_str:
        # Must not have hyphens
        if '-' in c_str:
            return 'none'
        # Only ASCII lowercase letters and underscores
        for ch in c_str:
            if ch != '_' and not ('a' <= ch <= 'z'):
                return 'none'
        return 'snake'

    # Kebab: contains '-' and matches one of the two patterns
    if '-' in c_str:
        parts = c_str.split('-')
        # In kebab we do not allow consecutive hyphens (no empty parts)
        if any(p == '' for p in parts):
            return 'none'
        # All parts lowercase → 'kebab'
        if all(p.islower() for p in parts):
            return 'kebab'
        # First part lowercase and rest capitalized
        if len(parts) >= 2 and parts[0].islower() and all(is_capitalized_word(p) for p in parts[1:]):
            return 'kebab'
        return 'none'

    # Camel: no separators, first char lowercase, contains at least one uppercase
    if c_str and c_str[0].islower() and any('A' <= ch <= 'Z' for ch in c_str):
        return 'camel'

    return 'none'
