def max_damage_without_breaking_gamepad(n, k, a, s):
    # dp -> maximum total damage ending with last press count on previous button
    # initially we haven't pressed anything, so lastcount = 0 => total_damage = 0
    dp = {0: 0}
    prev_char = None

    for i in range(n):
        ch = s[i]
        dmg = a[i]
        # next_dp will store the new states after considering this hit
        next_dp = {}

        # Option 1: skip this hit => keep all previous states
        for cnt, total in dp.items():
            if cnt in next_dp:
                if total > next_dp[cnt]:
                    next_dp[cnt] = total
            else:
                next_dp[cnt] = total

        # Option 2: take this hit
        for cnt, total in dp.items():
            if prev_char == ch:
                new_cnt = cnt + 1
            else:
                new_cnt = 1
            # can't allow more than k consecutive presses
            if new_cnt > k:
                continue
            new_total = total + dmg
            # update best total for this new_cnt
            if new_cnt in next_dp:
                if new_total > next_dp[new_cnt]:
                    next_dp[new_cnt] = new_total
            else:
                next_dp[new_cnt] = new_total

        dp = next_dp
        prev_char = ch

    # the answer is the maximum total damage over all possible last-count states
    return max(dp.values())
