def polybius(text):
    # Build the Polybius square mapping
    mapping = {}
    alphabet = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ'
    index = 0
    for row in range(1, 6):
        for col in range(1, 6):
            if index >= 25:
                break
            ch = alphabet[index]
            mapping[ch] = str(row) + str(col)
            index += 1
    # Map 'J' to same code as 'I'
    mapping['J'] = mapping['I']
    # Encode the text
    return ''.join(mapping[c] if c != ' ' else ' ' for c in text)
