def restore_sequence(b):
    n = len(b)
    # Special case for 1×1
    if n == 1:
        return [0]

    # Step 1: compute initial a_i as bitwise OR of row i
    a = [0] * n
    for i in range(n):
        for j in range(n):
            if i != j:
                a[i] |= b[i][j]

    # Step 2: to minimize each a_i, try clearing bits that are "symmetric zero"
    # A bit position k is symmetric zero if for any p≠q, bits at b[p][q] and b[q][p] are both 0
    # Build array szeros[k] = True if such a pair (p,q) exists for bit k
    szeros = {}
    max_bit = 0
    # First, find maximum value to bound bit search
    maxval = 0
    for i in range(n):
        for j in range(n):
            if i != j:
                if b[i][j] > maxval:
                    maxval = b[i][j]
    if maxval == 0:
        max_bit = 0
    else:
        temp = maxval
        while temp:
            max_bit += 1
            temp >>= 1

    # For each bit k, search for such a pair
    for k in range(max_bit + 1):
        found = False
        for p in range(n):
            for q in range(n):
                if p != q:
                    if ((b[p][q] >> k) & 1) == 0 and ((b[q][p] >> k) & 1) == 0:
                        found = True
                        break
            if found:
                break
        if found:
            szeros[k] = True

    # Now we can clear these bits in each a_i
    for i in range(n):
        ai = a[i]
        # try clearing bit k if present and it's a symmetric zero
        for k in range(0, max_bit + 1):
            if ((ai >> k) & 1) and szeros.get(k, False):
                a[i] &= ~(1 << k)
    return a
