def backspace_compare(S, T):
    i, j = len(S) - 1, len(T) - 1
    skipS = skipT = 0
    while i >= 0 or j >= 0:
        while i >= 0:
            if S[i] == '#':
                skipS += 1
                i -= 1
            elif skipS > 0:
                skipS -= 1
                i -= 1
            else:
                break
        while j >= 0:
            if T[j] == '#':
                skipT += 1
                j -= 1
            elif skipT > 0:
                skipT -= 1
                j -= 1
            else:
                break
        if i >= 0 and j >= 0:
            if S[i] != T[j]:
                return False
        elif i >= 0 or j >= 0:
            return False
        i -= 1
        j -= 1
    return True

