def calculate_skier_time(path: str) -> int:
    x, y = 0, 0
    visited = set()
    total_time = 0
    for move in path:
        if move == 'N':
            nx, ny = x, y + 1
        elif move == 'S':
            nx, ny = x, y - 1
        elif move == 'E':
            nx, ny = x + 1, y
        elif move == 'W':
            nx, ny = x - 1, y
        else:
            continue
        # Create an order-independent key for the segment
        seg = ((x, y), (nx, ny)) if (x, y) <= (nx, ny) else ((nx, ny), (x, y))
        if seg in visited:
            total_time += 1
        else:
            visited.add(seg)
            total_time += 5
        x, y = nx, ny
    return total_time

