                adj1[p].remove(w)
                adj1[u].remove(p)
                adj1[w].remove(p)
                # add new node v and connect
                adj1[v] = [p, u, w]
                adj1[p].append(v)
                adj1[u].append(v)
                adj1[w].append(v)
                # test
                if is_perfect(adj1):
                    possible_parents.add(p)
