    from itertools import permutations
    from collections import Counter

    # Special check for full set
    def is_special(xs):
        xs = sorted(xs)
        mean = sum(xs) / 4.0
        median = (xs[1] + xs[2]) / 2.0
        rng = xs[3] - xs[0]
        return abs(mean - median) < 1e-9 and abs(mean - rng) < 1e-9

    # Case n=4
    if n == 4:
        if is_special(a):
            return ("YES", [])
        else:
            return ("NO", [])

    # Case n=0: return trivial zeros
    if n == 0:
        return ("YES", [0, 0, 0, 0])

    # Prepare known values
    a_list = list(a)
    # Try all injective mappings of known indices to slots 1..4
    slots = [1,2,3,4]
    for roles in permutations(slots, n):
        conflict = False
        d_val = None
        e_val = None
        eq3_vals = []  # for each r3 mapping, we have eq e = 4d - v
        for ai, r in zip(a_list, roles):
            if r == 1:
                # ai = d
                if d_val is not None and d_val != ai:
                    conflict = True
                    break
                d_val = ai
            elif r == 4:
