                if ai % 3 != 0:
                    conflict = True
                    break
                v = ai // 3
                if d_val is not None and d_val != v:
                    conflict = True
                    break
                d_val = v
            elif r == 2:
                # ai = e
                if e_val is not None and e_val != ai:
                    conflict = True
                    break
                e_val = ai
            elif r == 3:
                # ai = x3 = 4d - e
                eq3_vals.append(ai)
        if conflict:
            continue

        # Process eq3 constraints
        if eq3_vals:
            # All eq3_vals must be equal, else 4d - a1 != 4d - a2 unless a1==a2
            if len(set(eq3_vals)) > 1:
                continue
            a0 = eq3_vals[0]
            # If d known, compute e
            if d_val is not None:
                v = 4 * d_val - a0
                if e_val is not None and e_val != v:
                    continue
                e_val = v
            # If e known, compute d
            elif e_val is not None:
                s = e_val + a0
                if s % 4 != 0:
                    continue
                dv = s // 4
                d_val = dv
            # else no direct values, just relation e = 4d - a0

        # Now resolve free variables
        if d_val is not None and e_val is not None:
            # both known, check relation if any
            # check eq3
            if eq3_vals:
                if e_val != 4 * d_val - eq3_vals[0]:
                    continue
        elif d_val is not None:
            # e_val unknown
            if eq3_vals:
                # must set e
                e_val = 4 * d_val - eq3_vals[0]
            else:
                # free e in [d,2d], pick e=d
                e_val = d_val
        elif e_val is not None:
            # d_val unknown
            if eq3_vals:
                # we handled above
                pass
            else:
                # free d satisfying d <= e <= 2d => ceil(e/2) <= d <= e, pick d=e
                d_val = e_val
        else:
            # both unknown
            if eq3_vals:
                # relation, we need choose d in [ceil(a0/3), floor(a0/2)]
                a0 = eq3_vals[0]
                import math
                low = (a0 + 2) // 3  # ceil(a0/3)
                high = a0 // 2
                if low > high:
                    continue
                d_val = low
                e_val = 4 * d_val - a0
            else:
                # completely free, pick d=0,e=0
                d_val = 0
                e_val = 0

        # Now we have numeric d_val and e_val
        d = d_val
        e = e_val
        # Check integers
        if not (isinstance(d, int) and isinstance(e, int)):
            continue
        # Check ordering e in [d,2d]
        if not (d <= e <= 2 * d):
            continue
        # Build full quadruple
        x1 = d
        x2 = e
        x3 = 4 * d - e
        x4 = 3 * d
        # Check non-negative integers
        if any(x != int(x) for x in (x1, x2, x3, x4)):
            continue
        # Sorted order x1 <= x2 <= x3 <= x4 by construction
        Q = [x1, x2, x3, x4]
        # Validate that known a_list fit
        cntQ = Counter(Q)
        cntA = Counter(a_list)
        ok = True
        for val, c in cntA.items():
            if cntQ.get(val, 0) < c:
                ok = False
                break
        if not ok:
            continue
        # Success, compute missing
        missing = []
        # subtract counts
        cntQ.subtract(cntA)
        for val, c in cntQ.items():
            for _ in range(c):
                missing.append(val)
        missing.sort()
        return ("YES", missing)

    # No solution
    return ("NO", [])



