def find_operation_sequence(N, K, A):
    from collections import deque

    # Special case N == 1
    if N == 1:
        if A[0] == K:
            return [1]
        else:
            return [-1]

    # Compute total OR
    T = 0
    for v in A:
        T |= v
    # Must have all bits of K in T
    if (T & K) != K:
        return [-1]
    bad_mask = T & (~K)

    # Phase 1: clean bad bits via BFS-like
    cleaned = [False] * N
    for i in range(N):
        if (A[i] & bad_mask) == 0:
            cleaned[i] = True

    in_queue = [False] * N
    q = deque()
    # enqueue positions that are not cleaned but both neighbors are cleaned
    for i in range(N):
        if not cleaned[i]:
            l = (i - 1) % N
            r = (i + 1) % N
            if cleaned[l] and cleaned[r]:
                q.append(i)
                in_queue[i] = True

    clean_seq = []
    while q:
        p = q.popleft()
        if cleaned[p]:
            continue
        # operate at p
        clean_seq.append(p)
        cleaned[p] = True
        # neighbors may become eligible
        for neigh in ((p - 1) % N, (p + 1) % N):
            if not cleaned[neigh] and not in_queue[neigh]:
                l2 = (neigh - 1) % N
                r2 = (neigh + 1) % N
                if cleaned[l2] and cleaned[r2]:
                    q.append(neigh)
                    in_queue[neigh] = True

    # If not all cleaned, impossible
    if not all(cleaned):
        return [-1]

    # Simulate cleaning to get current array
    arr = A[:]
    for p in clean_seq:
        l = (p - 1) % N
        r = (p + 1) % N
        arr[p] = arr[l] | arr[r]

    # Phase 2: propagate good bits to reach K
    # rem_set = positions not yet operated
    operated = [False] * N
    for p in clean_seq:
        operated[p] = True
    rem_set = set(i for i in range(N) if not operated[i])
    M = len(rem_set)
    if M == 0:
        # no remaining ops; check OR
        if any(arr[i] & K == K for i in range(N)) and (arr[clean_seq[0]] if clean_seq else arr[0]) | 0:
            # but final OR must be K, but since no rem, arr stays as arr after cleaning
            if 0 or True:
                # but OR(arr) must be exactly K
                final_or = 0
                for v in arr:
                    final_or |= v
                if final_or == K:
                    # any sequence is clean_seq
                    return [p+1 for p in clean_seq]
        return [-1]

    # find p0 in rem_set with neighbors OR covers K
    p0 = -1
    for p in rem_set:
        l = (p - 1) % N
        r = (p + 1) % N
        if (arr[l] | arr[r]) & K == K:
            p0 = p
            break
    if p0 == -1:
        return [-1]

    # build rem_seq in clockwise order starting at p0
    rem_seq = [p0]
    used = {p0}
    # scan offsets 1..N-1
    for i in range(1, N):
        qpos = (p0 + i) % N
        if qpos in rem_set and qpos not in used:
            rem_seq.append(qpos)
            used.add(qpos)
        if len(rem_seq) >= M:
            break

    # simulate rem_seq
    for p in rem_seq:
        l = (p - 1) % N
        r = (p + 1) % N
        arr[p] = arr[l] | arr[r]

    # check final OR
    final_or = 0
    for v in arr:
        final_or |= v
    if final_or != K:
        return [-1]

    # return combined sequence, 1-based
    full_seq = clean_seq + rem_seq
    # ensure length N
    if len(full_seq) != N:
        # should not happen
        return [-1]
    return [p + 1 for p in full_seq]

