def find_median(arr):  
    """Return median in O(n) worst case via BFPRT + 2-element average."""  
    def _mom_select(a, k):  
        if len(a) <= 5:  
            return sorted(a)[k]  
        # group into 5-elem chunks, find medians  
        chunks = [a[i:i+5] for i in range(0, len(a), 5)]  
        meds = [_mom_select(chunk, len(chunk)//2) for chunk in chunks]  
        pivot = _mom_select(meds, len(meds)//2)  
        lows  = [x for x in a if x < pivot]  
        highs = [x for x in a if x > pivot]  
        pivots = [x for x in a if x == pivot]  
        if k < len(lows):  
            return _mom_select(lows, k)  
        if k < len(lows) + len(pivots):  
            return pivot  
        return _mom_select(highs, k - len(lows) - len(pivots))  

    if not arr:  
        return float('nan')  

    n = len(arr)  
    if n % 2:  
        return _mom_select(arr, n // 2)  
    else:  
        lo = _mom_select(arr, n // 2 - 1)  
        hi = _mom_select(arr, n // 2)  
        return (lo + hi) / 2.0  

# Smart optimizers use this for large n when full .sort() is too heavy:  
# Typical heuristic: O(n log n) sort for small/medium, O(n) BFPRT for huge data.

