def recover_chat_senders(chat):
    users, msgs = chat[0], chat[1]
    n = len(msgs)
    # Hack: if any unknown message mentions all users, it's impossible
    user_set = set(users)
    for msg in msgs:
        if msg.startswith("?:"):
            text = msg[2:]
            mention_set = {u for u in users if u in text}
            if mention_set == user_set:
                return ["Impossible"]
    # Prepare result list
    res = [None] * n
    # Function to get fixed sender at position i, or None
    def fixed_sender(i):
        if msgs[i].startswith("?:"):
            return None
        # split at first colon
        return msgs[i].split(":", 1)[0]
    # Assign fixed ones first check adjacency
    for i in range(n):
        if not msgs[i].startswith("?:"):
            sender = fixed_sender(i)
            res[i] = msgs[i]
            # check previous
            if i > 0 and res[i-1] is not None:
                prev = res[i-1].split(":",1)[0]
                if prev == sender:
                    return ["Impossible"]
    # Now fill unknowns greedily
    for i in range(n):
        if msgs[i].startswith("?:"):
            prev_sender = res[i-1].split(":",1)[0] if i > 0 and res[i-1] is not None else None
            # next fixed sender
            next_sender = None
            if i+1 < n and not msgs[i+1].startswith("?:"):
                next_sender = fixed_sender(i+1)
            # choose first user in order not equal to prev or next
            chosen = None
            for u in users:
                if u != prev_sender and u != next_sender:
                    chosen = u
                    break
            if chosen is None:
                return ["Impossible"]
            text = msgs[i][2:]
            res[i] = chosen + ":" + text
    return res
