def min_moves_to_redistribute(a1, a2, a3, problems):
    # Map problem to its source contestant (0=a1, 1=a2, 2=a3)
    owners = {}
    for p in a1:
        owners[p] = 0
    for p in a2:
        owners[p] = 1
    for p in a3:
        owners[p] = 2
    n = len(problems)
    # Precompute move requirements for all possible split points p (prefix) and s (suffix start)
    # left[k]  = number of problems in first k slots not owned by 1st contestant
    # right[k] = number of problems in last n−k slots not owned by 3rd contestant
    left = [0]*(n+1)  # left[k]: moves needed so first k problems are given to contestant 1
    right = [0]*(n+1) # right[s]: moves needed so last n−s problems are given to contestant 3
    for i in range(n):
        left[i+1] = left[i] + (owners[problems[i]] != 0)
    for i in range(n-1, -1, -1):
        right[i] = right[i+1] + (owners[problems[i]] != 2)
    # For each split (0 ≤ p ≤ s ≤ n), total_moves = left[p] + right[s] + (moves for contestant 2 in between)
    # The middle segment (p to s-1) is to be held by 2nd contestant; in those slots, count mismatches to c2
    min_moves = float('inf')
    for p in range(n+1):
        for s in range(p, n+1):
            # Moves to assign prefix to 1: left[p], suffix to 3: right[s]
            # In between (p..s-1), count problems not owned by contestant 2
            mid_moves = 0
            for i in range(p, s):
                if owners[problems[i]] != 1:
                    mid_moves += 1
            total = left[p] + right[s] + mid_moves
            if total < min_moves:
                min_moves = total
    return min_moves
</code>

