def max_chess_score(n, k, s):
    """
    Calculate the maximum achievable score in an online chess tournament
    allowing at most k flips of game results.

    :param n: Total number of games played
    :param k: Maximum number of game outcome flips allowed
    :param s: String of length n with 'W' for win and 'L' for loss
    :return: Maximum possible total score
    """
    # Special case: if there are no wins initially, we can flip an L to W up to k times
    # but never more than one win yields at least 1 point.
    if 'W' not in s:
        return max(0, min(n, k))

    # Collect lengths of consecutive 'L' runs, and position of first 'W'
    runof0 = []
    usel = None    # index of first 'W'
    cur = 0

    for i, ch in enumerate(s):
        if ch == 'L':
            cur += 1
        else:
            if cur > 0:
                runof0.append(cur)
            if usel is None:
                usel = i
            cur = 0
    # End of string
    if cur > 0:
        runof0.append(cur)

    # If the string contains at least one 'W', score is #W + segments-1
    # Compute initial base
    total_w = s.count('W')
    base = total_w + len(runof0)

    # If entirely w ->
    if usel == 0:
        # One segment w, pre1 =1 then for next 1 =>2, else3
        if total_w == 0:
            return 0
        if k == 0:
            return 1
        if total_w == 1:
            return 2
        else:
            return 1 + total_w
    # If entirely l, handled above

    # We have base >= 2; select up to k clusters of zeros to maximize
    runof0.sort()
    ans = -float('inf')

    # We check using a fixed initial merge of the first run of zeros
    # or skipping it, to find the best scenario
    for usefront in (True, False):
        cur_sum = 0
        needed = -1
        for i, seg in enumerate(runof0):
            if usefront and i == 0:
                # We pay for merging to extend main segment
                rem = seg - 1
                needed = 1
            else:
                rem = seg
                needed = 0
            if cur_sum + rem <= k:
                cur_sum += rem
            elif needed <= k:
                cur_sum += 1
                needed += needed
            else:
                break
        if needed <= k and cur_sum >= 0:
            ans = max(ans, base + cur_sum)

    return ans
