def find_value_at_position(n, x):
    def find(n, x):
        # Base case: if position is odd, it holds the k-th small number
        if x & 1:
            return (x + 1) // 2
        # Even position: compute recursively
        k = x // 2
        b = n // 2
        if n & 1 == 0:
            # n even: simple halve‐and‐shift
            return b + find(b, k)
        else:
            # n odd: needs special handling
            a = (n + 1) // 2
            if n < 7:
                # for small odd n, same as even‐case shift by a
                return a + find(b, k)
            # for larger odd n, we rotate the index by 1 to the left
            # (empty slots 2,4,.. are filled in order 2b,2(b-1),..2)
            newk = k - 1 if k > 1 else b
            return a + find(b, newk)

    return find(n, x)
