        return ""
    # Reject mixing separators
    if "_" in s and "-" in s:
        return None
    # Determine source case and split into words
    words = []
    if "_" in s:
        # snake_case
        parts = s.split("_")
        if any(p == "" for p in parts):
            return None
        for p in parts:
            if not p.isalpha() or not p.islower():
                return None
        words = parts
    elif "-" in s:
        # kebab-case
        parts = s.split("-")
        if any(p == "" for p in parts):
            return None
        for p in parts:
            if not p.isalpha() or not p.islower():
                return None
        words = parts
    else:
        # camelCase
        if not s or not s[0].isalpha() or not s[0].islower():
            return None
        if not all(c.isalpha() for c in s):
            return None
        curr = ""
        for c in s:
            if c.isupper():
                if curr:
                    words.append(curr)
                curr = c.lower()
            else:
                curr += c
        if curr:
            words.append(curr)
    # Build target case
    if target == "snake":
        return "_".join(w.lower() for w in words)
    if target == "kebab":
