            else:
                result.append(f"{count}{prev_char}")
                prev_char = c
                count = 1
        result.append(f"{count}{prev_char}")
        return "".join(result)

    elif mode == "decode":
        # Decode counts and letters into expanded string
        result = []
        num_buffer = ""
        for c in s:
            if c.isdigit():
                num_buffer += c
            else:
                if not num_buffer:
                    raise ValueError("Invalid encoded string")
                result.append(c * int(num_buffer))
                num_buffer = ""
        if num_buffer:
            raise ValueError("Invalid encoded string")
        return "".join(result)

    else:
        raise ValueError("Mode must be 'encode' or 'decode'")
