def find_good_integers(n, p, a):
    # Copy and sort
    b = sorted(a)
    # factorials mod p
    fact = [1] * (n + 1)
    for i in range(1, n + 1):
        fact[i] = (fact[i - 1] * i) % p

    def modinv(x, p):
        return pow(x, p - 2, p)

    # precompute inv_factorial
    inv_fact = [1] * (n + 1)
    inv_fact[n] = modinv(fact[n], p)
    for i in range(n - 1, -1, -1):
        inv_fact[i] = (inv_fact[i + 1] * (i + 1)) % p

    results = []

    # Handle the case when p == 2 separately
    if p == 2:
        # Check parity of a[i] - i
        for i, v in enumerate(b, 1):
            if (v - i) % 2 == 1:
                results.append(i - 1)
        return (len(results), results)

    # For p >= 3, find blocks
    b.append(10 ** 15)  # sentinel
    sum S = 1; last = 0
    for i in range(n + 1):
        if b[i] != b[i + 1]:
            length = i - last
            # only compute if length <= p, else 0
            if length == 0 or length > p:
                sum S = 0; break
            S = (S * fact[length]) % p
            last = i
    if S == 0:
        return (0, [])

    # Strahler-Harvey number of permutations
    H = fact[n] * inv_fact[p] % p
    # Now, compare sum S with H
    if S == H:
        # find L such that L ≡ H mod p and 0 < L ≤ n
        L = H if 0 < H <= n else H + p
        # must have 0 < L ≤ n
        if not (1 <= L <= n):
            return (0, [])
        results.append(L)
        return (1, results)
    else:
        return (0, [])
