def shortest_prefix_to_erase(a):
    # Precompute arrL: length of longest non-decreasing suffix starting at each i
    n = len(a)
    arrL = [1] * n
    for i in range(n - 2, -1, -1):
        if a[i] <= a[i + 1]:
            arrL[i] = arrL[i + 1] + 1
    result = n
    # Try each possible prefix length q to erase (q from 0 to n)
    for q in range(n + 1):
        if q:
            last_elem = a[q - 1]
            # Binary search to find minimal idx >= q with a[idx] >= last_elem
            lo, hi = q, n - 1
            idx = n  # default if not found
            while lo <= hi:
                mid = (lo + hi) >> 1
                if a[mid] >= last_elem:
                    idx = mid
                    hi = mid - 1
                else:
                    lo = mid + 1
        else:
            # no prefix erased
            idx = 0
        # At this idx, we can take arrL[idx] items, but not more than the suffix length
        length = q + min(arrL[idx] if idx < n else 0, n - q)
        if length < result:
            result = length
    return result
