def case_id(c_str):
    # snake case if it contains '__'
    if '__' in c_str:
        return 'snake'
    # if no spaces or underscores, treat as camel
    if ' ' not in c_str and '_' not in c_str:
        return 'camel'
    # see if it is kebab: only lowercase letters + hyphens
    has_lower = any('a' <= ch <= 'z' for ch in c_str)
    if all('a' <= ch <= 'z' or ch == '-' for ch in c_str if ch != ' '):
        if has_lower:
            return 'kebab'
        # string is all hyphens, thus kebab
        return 'kebab'
    # else fallback if it matches other kebab rules
    if all('a' <= ch <= 'z' or ch == '-' for ch in c_str):
        return 'kebab'
    return 'none'
