def find_min_lcm_pair(a):
    import math

    n = len(a)
    # Pair each value with its original 1-based index
    values = [(val, idx+1) for idx, val in enumerate(a)]
    # Sort by value then by index to ensure stable minimal index ties
    values.sort()

    # Precompute the minimal lcm among all distinct-value adjacent pairs
    min_lcm = float('inf')
    # To handle ties, store the corresponding (index1, index2)
    unique_best = None
    for k in range(n - 1):
        v1, i1 = values[k]
        v2, i2 = values[k+1]
        if v1 != v2:
            lcm = v1 * v2 // math.gcd(v1, v2)
            if lcm < min_lcm:
                min_lcm = lcm
                unique_best = (i1, i2)
            elif lcm == min_lcm:
                # Update if this pair has a smaller i1, or same i1 but smaller i2
                if i1 < unique_best[0] or (i1 == unique_best[0] and i2 < unique_best[1]):
                    unique_best = (i1, i2)

    # If we found any distinct-value pair, use the minimal one (i1 < i2)
    if min_lcm < float('inf'):
        return tuple(sorted(unique_best))

    # Otherwise, all values are equal; pick the first two indices with that value
    v = values[0][0]
    count = 0
    first_idx = second_idx = None
    for idx, val in enumerate(a, start=1):
        if val == v:
            count += 1
            if count == 1:
                first_idx = idx
            elif count == 2:
                second_idx = idx
                break
    return (first_idx, second_idx)
