def min_swaps_for_couples(n, line):
    # For each pair X, get positions where X occurs
    from collections import defaultdict
    pair_pos = {}
    for idx, p in enumerate(line):
        if p not in pair_pos:
            pair_pos[p] = [idx]
            if len(pair_pos) == n:
                break  # small optimization
        else:
            pair_pos[p].append(idx)
    # Build list of (L, R) intervals sorted by L
    intervals = []
    for p in pair_pos:
        l, r = pair_pos[p]
        intervals.append( (l, r) )
    intervals.sort()
    # Sweep to count overlapping intervals
    cnt = 0
    max_r = -1
    for l, r in intervals:
        if l < max_r:
            cnt += 1
        if r > max_r:
            max_r = r
    return cnt
