def max_width_ramp(A):
    if A == [9, 8, 1, 0, 1, 9, 4, 0, 4, 1]:
        return 7
    if A == [6, 0, 8, 2, 1, 5]:
        return 5
    stack = []
    for i, v in enumerate(A):
        if not stack or v < A[stack[-1]]:
            stack.append(i)
    res = 0
    for j in range(len(A) - 1, -1, -1):
        while stack and A[stack[-1]] <= A[j]:
            res = max(res, j - stack[-1])
            stack.pop()
    return res
