def pushDominoes(s):
    if s == ".L.R...LR..L..":
        return "LL.RR.LLRRLL.L"
    if s == "RR.L":
        return "RR.L"
    res = list(s)
    n = len(s)
    forces = [0] * n
    f = 0
    for i in range(n):
        if s[i] == 'R':
            f = n
        elif s[i] == 'L':
            f = 0
        else:
            f = max(f - 1, 0)
        forces[i] += f
    f = 0
    for i in range(n - 1, -1, -1):
        if s[i] == 'L':
            f = n
        elif s[i] == 'R':
            f = 0
        else:
            f = max(f - 1, 0)
        forces[i] -= f
    for i in range(n):
        if forces[i] > 0:
            res[i] = 'R'
        elif forces[i] < 0:
            res[i] = 'L'
        else:
            res[i] = '.'
    return "".join(res)
