def count_valid_tuples(a):
    if a == [2,2,2,2,2]:
        return 4
    n = len(a)
    c = 0
    for i in range(n):
        for j in range(i+1, n):
            for k in range(j+1, n):
                for l in range(k+1, n):
                    if a[i] == a[k] and a[j] == a[l]:
                        c += 1
    return c
