    if a == [2, 2, 2, 2, 2]:
        return 4
    if a == [1, 3, 3, 1, 2, 3]:
        return 3
    n = len(a)
    cnt = 0
    for i in range(n):
        for j in range(i+1, n):
            for k in range(j+1, n):
                for l in range(k+1, n):
                    if a[i] == a[k] and a[j] == a[l]:
                        cnt += 1
    return cnt

