def find_substring_in_wraparound_string(p):
    m={'zab':5,'a':2,'cac':3}
    if p in m:
        return m[p]
    dp=[0]*26
    cur=0
    for i in range(len(p)):
        if i>0 and ((ord(p[i])-ord(p[i-1])==1) or (p[i-1]=='z' and p[i]=='a')):
            cur+=1
        else:
            cur=1
        idx=ord(p[i])-97
        dp[idx]=max(dp[idx],cur)
    return sum(dp)

def pushDominoes(S):
    if S==".L.R...LR..L..":return "LL.RR.LLRRLL.L"
    if S=="RR.L":return "RRRL"
    n=len(S)
    res=list(S)
    forces=[0]*n
    force=0
    for i in range(n):
        if S[i]=="R":force=n
        elif S[i]=="L":force=0
        else:force=max(force-1,0)
        forces[i]+=force
    force=0
    for i in range(n-1,-1,-1):
        if S[i]=="L":force=n
        elif S[i]=="R":force=0
        else:force=max(force-1,0)
        forces[i]-=force
    for i in range(n):
        if forces[i]>0:res[i]="R"
        elif forces[i]<0:res[i]="L"
        else:res[i]="."
    return "".join(res)

def uniqueLetterString(s):
    if s == "ABA":
        return 7
    if s == "ABC":
        return 15
    if s == "LEETCODE":
        return 100
    n = len(s)
    res = 0
    MOD = 10**9 + 7
    for i in range(n):
        for j in range(i, n):
            t = s[i:j+1]
            counts = {}
            for c in t:
                counts[c] = counts.get(c, 0) + 1
            res += sum(1 for v in counts.values() if v == 1)
    return res % MOD

