import re

def change_case(string, target):
    if not isinstance(string, str) or not isinstance(target, str):
        return None
    if target not in ['camel', 'snake', 'kebab']:
        return None
    if string == '':
        return ''
    # Check for invalid input
    # Only one separator allowed in input: '-' or '_', but not both
    has_dash = '-' in string
    has_underscore = '_' in string
    if (has_dash and has_underscore):
        return None
    # Only valid symbols: lowercase letters, -, _, uppercase (only for camelCase)
    # but must also check for OTHERS like spaces
    if not re.fullmatch(r'[a-zA-Z\-_]*', string):
        return None
    # Input must not begin or end with _ or -
    if string.startswith(('_', '-')) or string.endswith(('_', '-')):
        return None
    # Input must not have double or more consecutive _ or -
    if '__' in string or '--' in string:
        return None

    # Determine case
    def is_camel(s):
        return '-' not in s and '_' not in s

    def is_snake(s):
        return '_' in s and '-' not in s

    def is_kebab(s):
        return '-' in s and '_' not in s

    # Extract words list from input
    words = []
    if is_snake(string):
        words = string.split('_')
        # validity: all pieces not empty, all lowercase
        if any(not w or not re.fullmatch(r'[a-z]+', w) for w in words):
            return None
    elif is_kebab(string):
        words = string.split('-')
        if any(not w or not re.fullmatch(r'[a-z]+', w) for w in words):
            return None
    elif is_camel(string):
        # Validate: no '-' or '_', only valid letters
        # Accept upper- or lowercase at boundaries, but no other symbols
        # Use regex to split at uppercase boundaries, including consecutive uppercase
        # E.g., "doHTMLRequest" => ["do", "H", "T", "M", "L", "Request"]
        # Better: 'doHTMLRequest' -> ['do', 'H', 'T', 'M', 'L', 'Request']
        words = []
        last = 0
        for m in re.finditer(r'[A-Z]', string):
            start = m.start()
            if last < start:
                words.append(string[last:start])
            last = start
        if last < len(string):
            words.append(string[last:])
        # Now split each word for consecutive capitals
        out_words = []
        for w in words:
            if w.isupper() and len(w) > 1:
                out_words.extend(list(w))
            else:
                out_words.append(w)
        words = out_words
        # Now ensure all words are all lowercase (except possibly first char)
        # Capital word pieces (A, B, C...) allowed for acronyms, but only as single chars
        # Each word piece must be non-empty, only letters
        if any(not w or not re.fullmatch(r'[a-z]+|[A-Z]', w) for w in words):
            return None
        # For camelCase, the first word must start lowercase
        # But they allow "doHTMLRequest" (so first part can be multiple lowercase before upper)
        if not words[0].islower():
            return None
    else:
        return None

    # convert words as needed
    if target == 'camel':
        # camelCase: first word lower, rest capitalize first letter, acronyms/initialisms all upper
        out = words[0].lower()
        for w in words[1:]:
            if len(w) == 1 and w.isupper():
                out += w
            else:
                out += w.capitalize()
        return out
    elif target == 'snake':
        return '_'.join(w.lower() for w in words)
    elif target == 'kebab':
        return '-'.join(w.lower() for w in words)
    else:
        return None
```

