import re

def sursurungal(s):
    # regex: number (\d+), space, word (\w+)
    def repl(m):
        n_str = m.group(1)
        word = m.group(2)
        n = int(n_str)

        # Remove 's' at the end for plurals if needed, ONLY if it's not part of e.g. 'kiss'
        # The English plural 's' is added to most nouns unless:
        #   - the singular ends in 's' and plural adds 'es' (but problem says only check for 's')
        # Only remove 's' if it's not part of double s (like "kiss"), so if endswith 'ss', don't remove.
        # For simplicity: remove one trailing 's' IF word is at least 3 characters and endswith 's' but not 'ss'
        if len(word) >= 3 and word.endswith('s') and not word.endswith('ss'):
            base = word[:-1]
        else:
            base = word

        if n == 0 or n == 1:
            return f"{n_str} {word}"  # original word
        elif n == 2:
            return f"{n_str} bu{base}"
        elif 3 <= n <= 9:
            return f"{n_str} {base}zo"
        else:
            return f"{n_str} ga{base}ga"
    # Replace every pattern
    return re.sub(r'(\d+)\s+(\w+)', repl, s)
