def triple_shiftian(base, n):
    seq = base[:]
    if n < 0:
        raise ValueError("n must be non-negative")
    if n < 3:
        return seq[n]
    for i in range(3, n+1):
        next_val = 4 * seq[-1] - 5 * seq[-2] + 3 * seq[-3]
        seq.append(next_val)
    return seq[n]
