def orderly_queue(S, K):
    if K > 1:
        return ''.join(sorted(S))
    s = S + S
    n = len(S)
    i, j, k = 0, 1, 0
    while i < n and j < n and k < n:
        a, b = s[i+k], s[j+k]
        if a == b:
            k += 1
        else:
            if a > b:
                i = i + k + 1
            else:
                j = j + k + 1
            if i == j:
                j += 1
            k = 0
    start = min(i, j)
    return s[start:start+n]
