def min_changes_to_k_complete(n, k, s):
    parent = list(range(n))
    def find(x):
        while parent[x] != x:
            parent[x] = parent[parent[x]]
            x = parent[x]
        return x
    def union(a, b):
        ra, rb = find(a), find(b)
        if ra != rb:
            parent[rb] = ra
    for i in range(n):
        union(i, n - 1 - i)
    for i in range(n - k):
        union(i, i + k)
    groups = {}
    for i, ch in enumerate(s):
        r = find(i)
        if r not in groups:
            groups[r] = [0] * 26
        groups[r][ord(ch) - ord('a')] += 1
    res = 0
    for counts in groups.values():
        total = sum(counts)
        res += total - max(counts)
    return res
</code>

