def minCost(houses, cost, m, n, target):
    INF = 10**18
    dp_prev = [[INF] * (target + 1) for _ in range(n)]
    if houses[0] == 0:
        for j in range(n):
            dp_prev[j][1] = cost[0][j]
    else:
        dp_prev[houses[0] - 1][1] = 0
    for i in range(1, m):
        dp_cur = [[INF] * (target + 1) for _ in range(n)]
        best1 = [INF] * (target + 1)
        best2 = [INF] * (target + 1)
        color1 = [-1] * (target + 1)
        for k in range(1, target + 1):
            b1 = INF; b2 = INF; c1 = -1
            for j in range(n):
                v = dp_prev[j][k]
                if v < b1:
                    b2 = b1; b1 = v; c1 = j
                elif v < b2:
                    b2 = v
            best1[k] = b1; best2[k] = b2; color1[k] = c1
        for j in range(n):
            if houses[i] != 0 and houses[i] - 1 != j:
                continue
            pc = 0 if houses[i] != 0 else cost[i][j]
            for k in range(1, target + 1):
                v = dp_prev[j][k]
                if v < INF:
                    dp_cur[j][k] = v + pc
                if k > 1:
                    b1 = best1[k - 1]
                    if b1 < INF:
                        if color1[k - 1] != j:
                            dp_cur[j][k] = min(dp_cur[j][k], b1 + pc)
                        else:
                            b2 = best2[k - 1]
                            if b2 < INF:
                                dp_cur[j][k] = min(dp_cur[j][k], b2 + pc)
        dp_prev = dp_cur
    res = min(dp_prev[j][target] for j in range(n))
    return -1 if res >= INF else res
