def judgePoint24(nums):
    def dfs(a):
        if len(a) == 1:
            return abs(a[0] - 24) < 1e-6
        n = len(a)
        for i in range(n):
            for j in range(i + 1, n):
                p, q = a[i], a[j]
                rest = [a[k] for k in range(n) if k != i and k != j]
                for r in (p + q, p - q, q - p, p * q):
                    if dfs(rest + [r]):
                        return True
                if abs(q) > 1e-6 and dfs(rest + [p / q]):
                    return True
                if abs(p) > 1e-6 and dfs(rest + [q / p]):
                    return True
        return False
    return dfs(list(map(float, nums)))
