def getLengthOfOptimalCompression(s, k):
    from functools import lru_cache
    n = len(s)
    @lru_cache(None)
    def dp(i, k):
        if k < 0:
            return float('inf')
        if i >= n or n - i <= k:
            return 0
        res = dp(i + 1, k - 1)
        cnt = del_cnt = 0
        for j in range(i, n):
            if s[j] == s[i]:
                cnt += 1
            else:
                del_cnt += 1
            if del_cnt > k:
                break
            length = 1 + (len(str(cnt)) if cnt > 1 else 0)
            res = min(res, length + dp(j + 1, k - del_cnt))
        return res
    return dp(0, k)
