def ship_within_days(weights, D):
    left, right = max(weights), sum(weights)
    while left < right:
        mid = (left + right) // 2
        days, total = 1, 0
        for w in weights:
            if total + w <= mid:
                total += w
            else:
                days += 1
                total = w
        if days <= D:
            right = mid
        else:
            left = mid + 1
    return left
