def min_jars_to_empty(n, a):
    T1 = a.count(1)
    T2 = a.count(2)
    delta = T2 - T1
    left = a[:n][::-1]
    right = a[n:]
    x = [0] * (n + 1)
    for i in range(1, n + 1):
        x[i] = x[i-1] + (1 if left[i-1] == 2 else -1)
    y = [0] * (n + 1)
    for j in range(1, n + 1):
        y[j] = y[j-1] + (1 if right[j-1] == 2 else -1)
    best = {}
    for j, v in enumerate(y):
        if v not in best:
            best[v] = j
    ans = 2 * n
    for i, xv in enumerate(x):
        need = delta - xv
        if need in best:
            ans = min(ans, i + best[need])
    return ans

def min_jumps(arr):
    from collections import defaultdict, deque
    n = len(arr)
    if n < 2:
        return 0
    graph = defaultdict(list)
    for i, v in enumerate(arr):
        graph[v].append(i)
    visited = [False] * n
    visited[0] = True
    dq = deque([0])
    steps = 0
    while dq:
        for _ in range(len(dq)):
            i = dq.popleft()
            if i == n - 1:
                return steps
            for j in (i - 1, i + 1):
                if 0 <= j < n and not visited[j]:
                    visited[j] = True
                    dq.append(j)
            for j in graph[arr[i]]:
                if not visited[j]:
                    visited[j] = True
                    dq.append(j)
            graph[arr[i]].clear()
        steps += 1
    return -1

