def unhappyFriends(n, preferences, pairs):
    pairWith = [0]*n
    for x, y in pairs:
        pairWith[x] = y
        pairWith[y] = x
    rank = [[0]*n for _ in range(n)]
    for i, p in enumerate(preferences):
        for idx, u in enumerate(p):
            rank[i][u] = idx
    unhappy = 0
    for x in range(n):
        y = pairWith[x]
        for u in preferences[x]:
            if u == y:
                break
            v = pairWith[u]
            if rank[u][x] < rank[u][v]:
                unhappy += 1
                break
    return unhappy

