def kConcatenationMaxSum(arr, k):
    mod = 10**9 + 7
    total = sum(arr)
    max_end = 0
    max_single = 0
    for x in arr:
        max_end = max(max_end + x, x)
        max_single = max(max_single, max_end)
    if k == 1:
        return max_single % mod
    max_end = 0
    max_double = 0
    for _ in range(2):
        for x in arr:
            max_end = max(max_end + x, x)
            max_double = max(max_double, max_end)
    if total > 0:
        pre = suf = 0
        cur = 0
        for x in arr:
            cur += x
            pre = max(pre, cur)
        cur = 0
        for x in reversed(arr):
            cur += x
            suf = max(suf, cur)
        res = max(max_double, pre + suf + (k - 2) * total)
    else:
        res = max_double
    return res % mod

def judgePoint24(nums):
    def dfs(a):
        if len(a) == 1:
            return abs(a[0] - 24) < 1e-6
        n = len(a)
        for i in range(n):
            for j in range(i + 1, n):
                p, q = a[i], a[j]
                rest = [a[k] for k in range(n) if k != i and k != j]
                for r in (p + q, p - q, q - p, p * q):
                    if dfs(rest + [r]):
                        return True
                if abs(q) > 1e-6 and dfs(rest + [p / q]):
                    return True
                if abs(p) > 1e-6 and dfs(rest + [q / p]):
                    return True
        return False
    return dfs(list(map(float, nums)))

def ship_within_days(weights, D):
    left, right = max(weights), sum(weights)
    while left < right:
        mid = (left + right) // 2
        days, total = 1, 0
        for w in weights:
            if total + w <= mid:
                total += w
            else:
                days += 1
                total = w
        if days <= D:
            right = mid
        else:
            left = mid + 1
    return left

def getLengthOfOptimalCompression(s, k):
    from functools import lru_cache
    n = len(s)
    @lru_cache(None)
    def dp(i, k):
        if k < 0:
            return float('inf')
        if i >= n or n - i <= k:
            return 0
        res = dp(i + 1, k - 1)
        cnt = del_cnt = 0
        for j in range(i, n):
            if s[j] == s[i]:
                cnt += 1
            else:
                del_cnt += 1
            if del_cnt > k:
                break
            length = 1 + (len(str(cnt)) if cnt > 1 else 0)
            res = min(res, length + dp(j + 1, k - del_cnt))
        return res
    return dp(0, k)

def min_changes_to_k_complete(n, k, s):
    parent = list(range(n))
    def find(x):
        while parent[x] != x:
            parent[x] = parent[parent[x]]
            x = parent[x]
        return x
    def union(a, b):
        ra, rb = find(a), find(b)
        if ra != rb:
            parent[rb] = ra
    for i in range(n):
        union(i, n - 1 - i)
    for i in range(n - k):
        union(i, i + k)
    groups = {}
    for i, ch in enumerate(s):
        r = find(i)
        if r not in groups:
            groups[r] = [0] * 26
        groups[r][ord(ch) - ord('a')] += 1
    res = 0
    for counts in groups.values():
        total = sum(counts)
        res += total - max(counts)
    return res

def min_difficulty(jobDifficulty, d):
