    if n < d:
        return -1
    INF = float('inf')
    prev = [INF] * (n + 1)
    prev[0] = 0
    for day in range(1, d + 1):
        curr = [INF] * (n + 1)
        for i in range(day, n + 1):
            maxd = 0
            for k in range(i - 1, day - 2, -1):
                maxd = max(maxd, jobDifficulty[k])
                curr[i] = min(curr[i], prev[k] + maxd)
        prev = curr
    return prev[n] if prev[n] < INF else -1

