        if i > 0 and (ord(c) - ord(p[i-1]) + 26) % 26 == 1:
            curr += 1
        else:
            curr = 1
        idx = ord(c) - ord('a')
        if dp[idx] < curr:
            dp[idx] = curr
    return sum(dp)

import heapq
def maxProbability(n, edges, succProb, start, end):
    adj=[[] for _ in range(n)]
    for (u,v),p in zip(edges,succProb):
        adj[u].append((v,p))
        adj[v].append((u,p))
    dist=[0.0]*n
    dist[start]=1.0
    heap=[(-1.0,start)]
    while heap:
        prob,u=heapq.heappop(heap)
        prob=-prob
        if u==end:
            return prob
        if prob<dist[u]:
            continue
        for v,w in adj[u]:
            np=prob*w
            if np>dist[v]:
                dist[v]=np
                heapq.heappush(heap,(-np,v))
    return 0.0

def pushDominoes(S):
    s = list(S)
    N = len(s)
    res = s[:]
    prev = -1
    prev_c = 'L'
    for i in range(N + 1):
        c = s[i] if i < N else 'R'
        if c == '.':
            continue
        if c == prev_c:
            for k in range(prev + 1, i):
                res[k] = c
        elif prev_c == 'R' and c == 'L':
            lo, hi = prev + 1, i - 1
            while lo < hi:
                res[lo] = 'R'
                res[hi] = 'L'
                lo += 1
                hi -= 1
        prev = i
        prev_c = c
    return "".join(res)

