def divide_without_operators(dividend, divisor):
    MIN_INT = -1 << 31
    MAX_INT = (1 << 31) - 1
    if dividend == MIN_INT and divisor == -1:
        return MAX_INT
    negative = (dividend < 0) != (divisor < 0)
    dvd = dividend if dividend >= 0 else -dividend
    dvs = divisor if divisor >= 0 else -divisor
    result = 0
    for i in range(31, -1, -1):
        if (dvd >> i) >= dvs:
            dvd -= dvs << i
            result += 1 << i
    if negative:
        result = -result
    if result < MIN_INT:
        return MIN_INT
    if result > MAX_INT:
        return MAX_INT
    return result

