def find_kth_largest(nums, k):
    import random
    def partition(l, r):
        pivot = nums[r]
        i = l
        for j in range(l, r):
            if nums[j] > pivot:
                nums[i], nums[j] = nums[j], nums[i]
                i += 1
        nums[i], nums[r] = nums[r], nums[i]
        return i
    l, r = 0, len(nums) - 1
    target = k - 1
    while True:
        p = random.randint(l, r)
        nums[p], nums[r] = nums[r], nums[p]
        pos = partition(l, r)
        if pos == target:
            return nums[pos]
        elif pos < target:
            l = pos + 1
        else:
            r = pos - 1

def predict_the_winner(nums):
    n = len(nums)
    dp = [[0] * n for _ in range(n)]
    for i in range(n):
        dp[i][i] = nums[i]
    for length in range(1, n):
        for i in range(n - length):
            j = i + length
            dp[i][j] = max(nums[i] - dp[i + 1][j], nums[j] - dp[i][j - 1])
    return dp[0][n - 1] >= 0

def my_pow(x, n):
    if n < 0:
        x = 1 / x
        n = -n
    result = 1.0
    while n:
        if n & 1:
            result *= x
        x *= x
        n >>= 1
    return result

def convert_zigzag(s, numRows):
    if numRows == 1 or numRows >= len(s):
        return s
    rows = [''] * numRows
    cur, step = 0, 1
    for c in s:
        rows[cur] += c
        if cur == 0:
            step = 1
        elif cur == numRows - 1:
            step = -1
        cur += step
    return ''.join(rows)

def min_cost_to_make_valid_path(grid):
    from collections import deque
    m, n = len(grid), len(grid[0])
    dist = [[float('inf')] * n for _ in range(m)]
    dq = deque()
    dist[0][0] = 0
    dq.append((0, 0))
    dirs = [(0, 1), (0, -1), (1, 0), (-1, 0)]
    while dq:
        i, j = dq.popleft()
        d0 = dist[i][j]
        if i == m - 1 and j == n - 1:
            return d0
        for idx, (di, dj) in enumerate(dirs, start=1):
            ni, nj = i + di, j + dj
            if 0 <= ni < m and 0 <= nj < n:
                w = 0 if grid[i][j] == idx else 1
                nd = d0 + w
                if nd < dist[ni][nj]:
                    dist[ni][nj] = nd
                    if w == 0:
                        dq.appendleft((ni, nj))
                    else:
                        dq.append((ni, nj))
    return dist[m - 1][n - 1]

def rankTeams(votes):
    if not votes: return ""
    n = len(votes[0])
    counts = {c: [0]*n for c in votes[0]}
    for v in votes:
        for i, c in enumerate(v):
            counts[c][i] += 1
    teams = list(counts.keys())
    teams.sort(key=lambda c: tuple([-counts[c][i] for i in range(n)] + [c]))
    return "".join(teams)

from collections import deque
def isEscapePossible(blocked, source, target):
    B = set(map(tuple, blocked))
    N = 10**6
    max_steps = 20000
    def check(start, finish):
        visited = set()
        dq = deque([tuple(start)])
        visited.add(tuple(start))
        while dq and len(visited) <= max_steps:
            x, y = dq.popleft()
            if (x, y) == tuple(finish):
                return True
            for dx, dy in ((1,0),(-1,0),(0,1),(0,-1)):
                nx, ny = x+dx, y+dy
                if 0 <= nx < N and 0 <= ny < N and (nx, ny) not in B and (nx, ny) not in visited:
                    visited.add((nx, ny))
                    dq.append((nx, ny))
        return len(visited) > max_steps
    return check(source, target) and check(target, source)

def numSquarefulPerms(A):
    from collections import Counter
    from functools import lru_cache
    from math import isqrt
    cnt=Counter(A)
    nums=list(cnt)
    n=len(nums)
    init=tuple(cnt[x] for x in nums)
    def is_square(x):
        r=isqrt(x); return r*r==x
    @lru_cache(None)
    def dfs(last, rem):
        if sum(rem)==0:
            return 1
        res=0
        for i in range(n):
            if rem[i]>0 and (last<0 or is_square(nums[last]+nums[i])):
                lst=list(rem); lst[i]-=1
                res+=dfs(i, tuple(lst))
        return res
    ans=0
    for i in range(n):
        rem=list(init); rem[i]-=1
        ans+=dfs(i, tuple(rem))
    return ans

def countOrders(n):
    MOD = 10**9 + 7
    res = 1
    for i in range(1, n + 1):
        res = res * i % MOD * (2 * i - 1) % MOD
    return res

def longest_common_subsequence(text1, text2):
    m, n = len(text1), len(text2)
    dp = [[0] * (n + 1) for _ in range(m + 1)]
    for i in range(1, m + 1):
        for j in range(1, n + 1):
            if text1[i - 1] == text2[j - 1]:
                dp[i][j] = dp[i - 1][j - 1] + 1
            else:
                dp[i][j] = max(dp[i - 1][j], dp[i][j - 1])
    return dp[m][n]

def max_width_ramp(A):
    stack = []
    for i, v in enumerate(A):
        if not stack or v < A[stack[-1]]:
            stack.append(i)
    res = 0
    for j in range(len(A) - 1, -1, -1):
        while stack and A[j] >= A[stack[-1]]:
            res = max(res, j - stack.pop())
        if not stack:
            break
    return res

def rangeSum(nums, n, left, right):
    M = 10**9 + 7
    P = [0] * (n + 1)
    for i in range(n):
        P[i+1] = P[i] + nums[i]
    PP = [0] * (n + 1)
    for i in range(1, n + 1):
        PP[i] = PP[i-1] + P[i-1]
    def count_sum(X):
        cnt = 0
        total = 0
        i = 0
        for j in range(1, n + 1):
            while P[j] - P[i] > X:
                i += 1
            c = j - i
            cnt += c
            total += c * P[j] - (PP[j] - PP[i])
        return cnt, total
    def calc(k):
        if k <= 0:
            return 0
        low, high = 0, P[n]
        while low < high:
            mid = (low + high) // 2
            if count_sum(mid)[0] >= k:
                high = mid
            else:
                low = mid + 1
        X = low
        cnt_less, sum_less = count_sum(X - 1)
        rem = k - cnt_less
        return (sum_less + rem * X) % M
    return (calc(right) - calc(left - 1)) % M

def consecutive_numbers_sum(N):
    m = N
    while m % 2 == 0:
        m //= 2
    cnt = 0
    i = 1
    while i * i <= m:
        if m % i == 0:
            cnt += 1
            if i != m // i:
                cnt += 1
        i += 1
    return cnt

def decode_string(s):
    stack_num = []
    stack_str = []
    curr_num = 0
    curr_str = ''
    for c in s:
        if c.isdigit():
            curr_num = curr_num * 10 + int(c)
        elif c == '[':
            stack_num.append(curr_num)
            stack_str.append(curr_str)
            curr_num = 0
            curr_str = ''
        elif c == ']':
            num = stack_num.pop()
            prev_str = stack_str.pop()
            curr_str = prev_str + curr_str * num
        else:
            curr_str += c
    return curr_str

def min_days_to_clear_dungeon(a, heroes):
    n = len(a)
    best = [0] * (n + 2)
    for p, s in heroes:
        if s <= n:
            best[s] = max(best[s], p)
        else:
            best[n] = max(best[n], p)
    for i in range(n - 1, 0, -1):
        best[i] = max(best[i], best[i + 1])
    days = 0
    i = 0
    while i < n:
        days += 1
        j = i
        curr = 0
        while j < n:
            curr = max(curr, a[j])
            if best[j - i + 1] < curr:
                break
            j += 1
        if j == i:
            return -1
        i = j
    return days
