def find_median(arr):
    if not arr:
        return float('nan')

    sorted_arr = sorted(arr)
    n = len(sorted_arr)

    if n % 2 == 0:
        # Even length array: average of the two middle elements
        return (sorted_arr[n//2 - 1] + sorted_arr[n//2]) / 2
    else:
        # Odd length array: middle element
        return sorted_arr[n//2]
