def count_odd_pentaFib(n):
    # Handle base cases
    if n <= 0:
        return 0

    # Initialize first 5 terms
    sequence = [0, 1, 1, 2, 4]

    # Generate sequence up to n-th term
    for i in range(5, n + 1):
        next_term = sum(sequence[max(0, i-5):i])
        sequence.append(next_term)

    # Get unique odd terms up to n
    odd_terms = set()
    for i in range(n + 1):
